// Compile comprehensive teacher resource package
const formData = $('Teacher Input Form').item.json;
const lessonContent = $('Content Creation Agent').item.json.output;
const assessmentTools = $('Assessment & Marking Agent').item.json.output;
const aiRecommendations = $('AI Integration Agent').item.json.output;

const timestamp = new Date().toISOString();
const packageId = `lesson_${formData.Subject.toLowerCase().replace(/\s+/g, '_')}_${formData['Year Group'].replace(/\s+/g, '_')}_${Date.now()}`;

// Extract key metrics and recommendations
function extractTimeSavings(text) {
  const timeMatches = text.match(/(\d+)\s*(hours?|minutes?|mins?).*?saved?/gi) || [];
  return timeMatches.slice(0, 5);
}

function extractToolRecommendations(text) {
  const tools = [];
  const commonTools = [
    'Google Forms', 'Kahoot', 'Quizizz', 'Padlet', 'Mentimeter',
    'Canva', 'PowerPoint', 'Prezi', 'Flipgrid', 'Seesaw',
    'ClassDojo', 'Google Classroom', 'Microsoft Teams', 'Zoom',
    'Grammarly', 'ChatGPT', 'Claude', 'Bard', 'QuillBot'
  ];

  commonTools.forEach(tool => {
    if (text.toLowerCase().includes(tool.toLowerCase())) {
      tools.push(tool);
    }
  });

  return [...new Set(tools)];
}

// Compile the complete package
const teacherPackage = {
  metadata: {
    package_id: packageId,
    created_at: timestamp,
    subject: formData.Subject,
    year_group: formData['Year Group'],
    topic: formData['Topic/Chapter'],
    curriculum_standard: formData['Curriculum Standard'],
    lesson_duration: formData['Lesson Duration (minutes)'],
    class_size: formData['Class Size'],
    differentiation_profile: formData['Differentiation Needs'],
    assessment_focus: formData['Assessment Focus'],
    package_type: 'comprehensive_modern_lesson'
  },

  lesson_package: {
    content: lessonContent,
    type: 'textbook_replacement',
    curriculum_aligned: true,
    differentiation_included: true,
    parent_support_included: true
  },

  assessment_tools: {
    content: assessmentTools,
    marking_efficiency_focus: true,
    automated_feedback_included: true,
    time_saving_strategies: extractTimeSavings(assessmentTools)
  },

  ai_automation: {
    content: aiRecommendations,
    recommended_tools: extractToolRecommendations(aiRecommendations),
    implementation_ready: true,
    workload_reduction_focus: true
  },

  implementation_guide: {
    immediate_actions: [
      '1. Review complete lesson package and select appropriate differentiated materials',
      '2. Print/prepare physical resources and set up digital tools',
      '3. Familiarize yourself with assessment rubrics and marking codes',
      '4. Set up recommended AI tools and automation workflows',
      '5. Prepare parent communication materials'
    ],

    time_management: {
      preparation_time: '20-30 minutes (vs 2-3 hours traditional)',
      marking_time_saved: '50-70% reduction with provided tools',
      resource_creation: 'Pre-made materials eliminate creation time',
      differentiation: 'All ability levels catered for automatically'
    }
  }
};

return { json: teacherPackage };

const packageData = $('Package Compiler').item.json;

// Create comprehensive markdown document
let markdown = `# Modern Lesson Package: ${packageData.metadata.topic}

`;
markdown += `**Subject:** ${packageData.metadata.subject}
`;
markdown += `**Year Group:** ${packageData.metadata.year_group}
`;
markdown += `**Duration:** ${packageData.metadata.lesson_duration} minutes
`;
markdown += `**Curriculum:** ${packageData.metadata.curriculum_standard}

`;

markdown += `## 📚 Complete Lesson Package

`;
markdown += packageData.lesson_package.content + '

';

markdown += `## 📊 Assessment & Marking Tools

`;
markdown += packageData.assessment_tools.content + '

';

markdown += `## 🤖 AI Integration Recommendations

`;
markdown += packageData.ai_automation.content + '

';

return [{ json: { markdown, package_id: packageData.metadata.package_id } }];
