// cleanAnswer ‚Äì run once per item
let txt = $input.first().json.output || '';

// 1. Remove bold / italic / strike markers
txt = txt.replace(/[*_~]+/g, '');

// 2. Convert [Texto](https://url) ‚Üí Texto https://url
txt = txt.replace(/\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g, '$1 $2');

// 3. Collapse 3+ blank lines
txt = txt.replace(/\n{3,}/g, '\n\n').trim();

return [{ json: { answer: txt } }];

// within24h?  ‚Äì run once per item
// Meta (WhatsApp) timestamp arrives as seconds since epoch
const lastTs = Number($('WhatsApp Trigger').first().json.messages[0].timestamp) * 1000;   // ‚Üí ms
const withinWindow = Date.now() - lastTs < 24 * 60 * 60 * 1000;

return [{ json: { withinWindow, answer: $json.answer, userId: $json.userId } }];
